mapper.select:
SELECT 

FROM oa_task_record a

WHERE a.id = #{id}

SELECT 

FROM oa_task_record a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.oa_task_id = #{oaTaskId}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}

SELECT 

FROM oa_task_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE oa_task_record SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE oa_task_id = #{oaTaskId}
