mapper.select:
SELECT 

FROM purchase_receipt a

WHERE a.id = #{id}

SELECT 

FROM purchase_receipt a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.supplier = #{supplier}


		AND a.goods_name = #{goodsName}


		AND a.goods_type = #{goodsType}


		AND a.unit_price = #{unitPrice}


		AND a.goods_num = #{goodsNum}


		AND a.total_price = #{totalPrice}


		AND a.rec_date BETWEEN #{beginRecDate} AND #{endRecDate}


		AND a.purchase_person = #{purchasePerson}


		AND a.pay_method = #{payMethod}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM purchase_receipt a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE purchase_receipt SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
