mapper.select:
SELECT 

FROM supplier a

WHERE a.id = #{id}

SELECT 

FROM supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.sup_name = #{supName}


		AND a.reg_capital = #{regCapital}


		AND a.legal_person LIKE 
			'%'||#{legalPerson}||'%'
			'%'+#{legalPerson}+'%'
			concat('%',#{legalPerson},'%')


		AND a.sup_nature = #{supNature}


		AND a.sup_url = #{supUrl}


		AND a.sup_address = #{supAddress}


		AND a.set_time BETWEEN #{beginSetTime} AND #{endSetTime}


		AND a.zip_code = #{zipCode}


		AND a.contact LIKE 
			'%'||#{contact}||'%'
			'%'+#{contact}+'%'
			concat('%',#{contact},'%')


		AND a.sup_email = #{supEmail}


		AND a.sup_tel = #{supTel}


		AND a.fax_num = #{faxNum}


		AND a.bank_name = #{bankName}


		AND a.bank_num = #{bankNum}


		AND a.credit_rating = #{creditRating}


		AND a.business_scope = #{businessScope}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE supplier SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
